import { Component, OnInit } from '@angular/core';
import { ApiService } from '../../services/api.service';
import { Artist } from '../../models/models';

@Component({
  selector: 'app-artist-list',
  templateUrl: './artist-list.component.html',
  styleUrls: ['./artist-list.component.css']
})
export class ArtistListComponent implements OnInit {
  artists: Artist[] = [];
  filteredArtists: Artist[] = [];
  loading = true;
  searchTerm = '';

  constructor(private apiService: ApiService) {}

  ngOnInit() {
    this.loadArtists();
  }

  loadArtists() {
    this.apiService.getArtists().subscribe({
      next: (data) => {
        this.artists = data;
        this.filteredArtists = data;
        this.loading = false;
      },
      error: (error) => {
        console.error('Error loading artists:', error);
        this.loading = false;
      }
    });
  }

  onSearch() {
    if (!this.searchTerm.trim()) {
      this.filteredArtists = this.artists;
      return;
    }

    const term = this.searchTerm.toLowerCase();
    this.filteredArtists = this.artists.filter(artist =>
      artist.name.toLowerCase().includes(term) ||
      artist.specialty.toLowerCase().includes(term) ||
      artist.location.toLowerCase().includes(term)
    );
  }
}
<div class="artist-list">
  <div class="container">
    <div class="page-header fade-in">
      <h1>Discover Artists</h1>
      <p>Browse through our talented community of artists from around the world</p>
    </div>

    <div class="search-bar fade-in">
      <input 
        type="text" 
        [(ngModel)]="searchTerm"
        (input)="onSearch()"
        placeholder="Search artists by name, specialty, or location..."
        class="search-input">
      <span class="search-icon">üîç</span>
    </div>

    <div *ngIf="loading" class="loading-container">
      <div class="loading"></div>
      <p>Loading artists...</p>
    </div>

    <div *ngIf="!loading && filteredArtists.length === 0" class="no-results">
      <div class="no-results-icon">üòî</div>
      <h3>No artists found</h3>
      <p>Try adjusting your search terms</p>
    </div>

    <div *ngIf="!loading && filteredArtists.length > 0" class="artists-grid">
      <div *ngFor="let artist of filteredArtists" 
           class="artist-card fade-in"
           [routerLink]="['/artists', artist.id]">
        <div class="artist-avatar">
          <img [src]="artist.avatar" [alt]="artist.name">
          <div class="artist-badge">{{ artist.specialty }}</div>
        </div>
        <div class="artist-info">
          <h3>{{ artist.name }}</h3>
          <p class="artist-location">üìç {{ artist.location }}</p>
          <p class="artist-bio">{{ artist.bio }}</p>
          <div class="artist-stats">
            <span class="stat">
              <strong>{{ artist.followers | number }}</strong> Followers
            </span>
            <span class="stat">
              <strong>{{ artist.artworkCount || 0 }}</strong> Artworks
            </span>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>
